mapper.update:
update t_role
set del_flag = -1
where id = #{id}

mapper.select:
select
from t_role where del_flag = 0
and role_name like concat('%',#{roleName},'%')
and enable_status = #{enableStatus}
and role_visibility = #{roleVisibility}
order by id desc

select * from ( select tr1.* from t_role tr1 where tr1.del_flag = 0 and tr1.role_visibility = 0 union select tr.* from t_role tr, t_role_tenant trt where tr.del_flag = 0 and trt.del_flag = 0 and tr.role_visibility = 2 and tr.id = trt.role_id and trt.tenant_id in
#{tenantId}
) tall where 1=1
and role_name like concat('%',#{roleName},'%')
and enable_status = #{enableStatus}
order by id desc

select
from t_role where del_flag = 0 and id in
#{id}

select tr.* from t_role tr,t_role_tenant trt where tr.del_flag = 0 and trt.del_flag = 0 and tr.enable_status = 1 and tr.id = trt.role_id and trt.tenant_id in
#{tenantId}
order by tr.id
