mapper.select:
select
from t_user where del_flag = 0 and tenant_id in
#{item}

select tu.id orgId, tu.department_id parentId, 1 depth, tu.tenant_id tenantId, concat(tu.account, '/', tu.username) orgName, 0 isRoot, 'u' type, tu.id orderNum from t_user tu where del_flag = 0 and department_id in
#{departmentId}
and tu.id in
#{item}
order by tu.id desc

select *
from t_user
where del_flag = 0
  and department_id = #{departmentId}
  and id  <>  #{userId}

select count(account)
from t_user
where account = #{account}
  and tenant_id = #{tenantId}
  and del_flag = 0

select *
from t_user
where account = #{account}
  and tenant_id = #{tenantId}
  and del_flag = 0

select

from t_user where del_flag = 0

    and (
    account like CONCAT('%',#{searchField},'%')
    or
    phone_number like CONCAT('%',#{searchField},'%')
    or
    username like CONCAT('%',#{searchField},'%')
    or
    id = #{searchField}
    )


    and id = #{userId}


    and enable_status = #{userStatus}


    and tenant_id in

        #{item}



    and department_id in

        #{item}



    and id in

        #{item}


order by id desc

mapper.update:
update t_user
set del_flag = -1
where id = #{id}

update t_user
set password = #{password}
where id = #{id}

mapper.select:
select count(*) from t_user
where del_flag = 0

    and department_id = #{departmentId}


    and department_id in

        #{departmentId}

select tu.id id, tu.account account, tu.username username, tu.department_id departmentId, tu.superior_id superiorId, tu.phone_number phoneNumber, tu.local_type localType, tu.enable_status enableStatus, tu.created_date createdDate from t_user tu, t_user_path tup where tu.del_flag = 0 and tup.del_flag = 0 and tu.id = tup.sub_id
and tup.superior_id = #{userId}
and tu.enable_status = #{userStatus}
and tu.id in
#{item}
and tu.department_id in
#{item}
and ( tu.account like CONCAT('%',#{searchField},'%') or tu.phone_number like CONCAT('%',#{searchField},'%') or tu.username like CONCAT('%',#{searchField},'%') )
